import React, { Component } from 'react';
import { connect } from 'react-redux';

// import FrontBody from './FrontBody';
import FaceBody from './FaceBody';

class Body extends Component {
  shouldComponentUpdate(nextProps) {
    return (nextProps.body !== this.props.body);
  }

  bodyParts = () => {
    if (this.props.body === "back") {
      return <FaceBody backBodyViewBox={this.props.backBodyViewBox} bodySide="back" />;
    }
    return <FaceBody frontBodyViewbox={this.props.frontBodyViewbox} bodySide="front" />;
  }

  render() {
    console.log("render");
    return (
      <div className="svg-body">
        {this.bodyParts()}
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    body: state.body
  };
}

export default connect(mapStateToProps)(Body);
